	var a = this.a;
	var b = this.b;
	this.r1 = vrotate(this.anchr1, a.rot);
	this.r2 = vrotate(this.anchr2, b.rot);
	k_tensor(a, b, this.r1, this.r2, this.k1, this.k2);
	this.jMaxLen = this.maxForce * dt;
	var delta = vsub(vadd(b.p, this.r2), vadd(a.p, this.r1));
